(function(){App={init:function(){var request=new XMLHttpRequest;request.open("GET","dist/js/data.json",true);request.onreadystatechange=function(){if(this.readyState===4){if(this.status>=200&&this.status<400){var data=JSON.parse(this.responseText);App.showInfo(data)}else{}}};request.send();request=null},showInfo:function(data){App.runHandlebars(data)},runHandlebars:function(data){var source=document.getElementById("template").innerHTML;var template=Handlebars.compile(source);var html=template(data);document.getElementById("portfolio").innerHTML=html}};function ready(fn){if(document.readyState!="loading"){fn()}else if(document.addEventListener){document.addEventListener("DOMContentLoaded",fn)}else{document.attachEvent("onreadystatechange",function(){if(document.readyState!="loading")fn()})}}ready(App.init)})();
